local sqlite = require ( "sqlite3" )local M = {}local function hintUse(playerID, curCoins, numHintUse )	local playerID = playerID	local curCoins = curCoins	local numHintUse = numHintUse	local path = system.pathForFile ("playerDB.sqlite",system.DocumentsDirectory)	local db = sqlite.open(path)		local sql = "UPDATE player_Data SET current_Coins =" .. curCoins .. ", num_Hints_Used =" .. numHintUse .. " WHERE player_ID = " .. playerID	db:exec(sql)	db:close()endM.hintUse = hintUse	return M 