local sqlite = require ( "sqlite3" )local M = {}local function upCorWro(playerID,fieldName,dataTo)	local playerID = playerID	local fieldName = fieldName	local dataTo = dataTo	local path = system.pathForFile ("playerDB.sqlite",system.DocumentsDirectory)	local db = sqlite.open(path)		local sql = "UPDATE player_Data SET " .. fieldName .. " = " .. dataTo .. " WHERE player_ID = " .. playerID		db:exec(sql)	db:close()	endM.upCorWro = upCorWro	return M