local sqlite = require ( "sqlite3" )local M = {}local function upLevel(playerID,newLevel,newEra)		local playerID = playerID	local newLevel = newLevel	local newEra = newEra		local path = system.pathForFile ("playerDB.sqlite", system.DocumentsDirectory)	local db = sqlite.open(path)		local sql = "UPDATE player_Data SET current_Level = " .. newLevel .. " , current_Era = " .. newEra .. " WHERE player_ID = " .. playerID		db:exec(sql)	db:close()	print ("level successfully updated")endM.upLevel = upLevel	return M 